import React, { Component } from "react";
// import What from './What/What';
import classes from "./Article.module.css";
import axios from "../../axios";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

function Article(props) {
  let openArticle = id => {
    axios.get(`full-articles/${id}.json`).then(response => {
      props.loadFullArticle(response.data);
    });
  };
  return (
    <div className="Article">
      <div className={classes.Article}>
        <img src={props.preview} />
        <div>
          <h2 onClick={openArticle(props.id)}>
            <Link to="/full-article">{props.title}</Link>
          </h2>

          <p>{props.content}</p>
        </div>
      </div>
    </div>
  );
}

const mapDispatchToProps = dispatch => {
  return {
    loadFullArticle: content => dispatch({ type: "LOAD_FULL_ARTICLE", content })
  };
};

// const mapStateToProps = state => {
//   return {
//     // this.props.ingredients: reducer.js/state.ingredients
//     articles: state.articles
//   };
// };

export default connect(
  null,
  mapDispatchToProps
)(Article);
