const initialState = {
  articles: []
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOAD_DATA":
      let articles = [];
      for (let id in action.data) {
        orders.push({ id, ...action.data[id] });
      }

      this.setState({ orders, loading: false });
      return {
        ...state
        //articles: action.data
      };

    default:
      return { ...state };
  }
};

export default reducer;
